  // //   {Financial:"Total Asset",Operational:"Headcount",Categorical:"Subsector"},
  // //   {Financial:""}
  // ]
  return (
    <div style={style}>
      <h2 style={{padding:"30px"}}>Methodology</h2>
      <div class="method-content">
        <Methoditems/>
      </div>

      {/* <div class="method-content2" style={{paddingTop:"20px"}}>
        <Methoditems2/>
      </div> */}
    </div>

  )
}

export default Methodology
