import React from 'react'
import background from "../assets/background.JPEG"

const Motivations = () => {
  const style = {
    //Now working
    backgroundImage: `url(${background})`,
    height: "100vh",
    backgroundSize: "cover",
}
  return (
    <div style={style}>
      <h1 style={{paddingTop:'70px',paddingBottom:"10px"}}>Project Motivations</h1>
      <h4 style={{marginRight:1000,paddingBottom:"10px"}}>Industry Outlook</h4>
      <img style={{width:500,height:350, marginRight:1000}} src={require('../assets/outlook.png')}></img>
      <h6 style={{marginRight: 1000, paddingTop:"20px"}}>
       `According to the International Energy Agency, global oil demand could {'\n'} 
        decrease by more than 50% over the next 25 years due to global efforts {'\n'}
        in achieving NZE. As such, SCB faces the challenge of redistributing {'\n'}
        investments towards more sustainable companies.`</h6>
    </div>

  )
}

export default Motivations
